frappe.ready(() => {

    // Wait until .web-form-actions exists
    function waitForActions(callback) {
        const checkExist = setInterval(() => {
            if ($(".web-form-actions").length > 0) {
                clearInterval(checkExist);
                callback();
            }
        }, 100);
    }

    waitForActions(() => {

        // Insert placeholder before submit button
        if (!$("#checklist-section").length) {
            $(".web-form-actions").before(`
                <div id="checklist-section" class="mt-4 mb-4"></div>
            `);
        }

        function getCheckIcon(value) {
            return value ? '<i class="fa-regular fa-check-square"></i>'
                         : '<i class="fa-regular fa-square"></i>';
        }

        function renderChecklist() {
            const doc = frappe.web_form.get_values() || {};

            const html = `
                <div class="section-title">
                    <i class="fa-solid fa-paperclip fa-icon"></i> 
                    CHECKLIST: REQUIRED DOCUMENTS (To Be Submitted)
                </div>

                <div class="checkbox-list">
                    <div class="checkbox-item">${getCheckIcon(doc.custom_birth_certificate)} Birth Certificate</div>
                    <div class="checkbox-item">${getCheckIcon(doc.custom_applicants_passport)} Passport Photographs (2 copies)</div>
                    <div class="checkbox-item">${getCheckIcon(doc.custom_previous_school_result)} Academic Reports (Last 2 Terms)</div>
                    <div class="checkbox-item">${getCheckIcon(doc.custom_transfer_certificate)} Transfer Certificate (for transfer students)</div>
                    <div class="checkbox-item">${getCheckIcon(doc.custom_imm)} Medical & Immunization Record</div>
                    <div class="checkbox-item">${getCheckIcon(doc.custom_fathers_id_card || doc.custom_mothers_id_card)} Parent/Guardian ID</div>
                    <div class="checkbox-item">${getCheckIcon(doc.custom_fathers_passport || doc.custom_mothers_passport)} Parents Passport Photograph</div>
                    <div class="checkbox-item">${getCheckIcon(doc.custom_emergency_contact_passport)} Emergency Contact Person Passport</div>
                </div>
            `;

            $("#checklist-section").html(html);
        }

        // Render checklist immediately
        renderChecklist();

        // Re-render when fields change
        [
            "custom_birth_certificate",
            "custom_applicants_passport",
            "custom_previous_school_result",
            "custom_transfer_certificate",
            "custom_imm",
            "custom_fathers_id_card",
            "custom_mothers_id_card",
            "custom_fathers_passport",
            "custom_mothers_passport",
            "custom_emergency_contact_passport"
        ].forEach(field => {
            frappe.web_form.on(field, renderChecklist);
        });

    });
});

